import React from 'react';
import { Card, WingBlank, WhiteSpace,List,Badge} from 'antd-mobile';
// import {Link}from'react-router-dom'
import banner from '@/assets/img/home-banner.png'
import {infoListMatch} from '@/plugins/resurceStatus/infoList'
const Item = List.Item;
const Brief = Item.Brief;

class HomePage extends React.Component {
  constructor(...args) {
    super(...args);
    this.state={
      ...infoListMatch('0')
    }
  }
  render() {
    return (
      <div className="qm-fill-width" style={{backgroundColor:"#f5f5f9"}}>
    <WingBlank size="lg">
    <WhiteSpace size="lg" />
    <Card><img src={banner}></img></Card>
    <WhiteSpace size="lg" />
    <List>
        <Item
          arrow="horizontal"
          thumb={this.state.icon}
          multipleLine
          onClick={() => {}}
        >
          {this.state.title} <Brief>{this.state.text}</Brief>
        </Item>
      </List>
      <WhiteSpace size="lg" />
      {['1','2','3','4','5'].map(item=>{
        return(
          <div key={item}>
          <List>
            <Item
              style={{overflow:"auto"}}
              extra={item+"  10:30"}
              thumb={infoListMatch(item).icon}
              multipleLine
              onClick={() => {}}
            >
              {infoListMatch(item).title} <Badge style={{marginTop:"-16px",zIndex:10,position:"absolute"}} text={7+parseInt(item)} overflowCount={10} /><Brief>李宇轩完成了任务，资料已上传完成，请...</Brief>
            </Item>
          </List>
          <WhiteSpace size="lg" />
          </div>
        )
      })}
    <WhiteSpace size="lg" />
  </WingBlank>
      </div>
    );
  }
}

export default HomePage;
