import { TabBar } from 'antd-mobile';
import React,{Component}from 'react'
import Home from '@/components/main/Home'
import My from '@/components/main/My'
import Task from '@/components/main/Task'

import {Link}from 'react-router-dom'
import {HomeOutlined,UserOutlined,CopyOutlined, UsergroupAddOutlined } from '@ant-design/icons'

class Main extends Component{
  constructor(...args) {
    super(...args);
    this.state = {
      selectedTab: 'blueTab',
      hidden: false
    };
  }
  componentDidMount(){
    console.log("mounted")
  }
  componentDidUpdate(){
    console.log("update")
  }
  render(){
    return (
      <div className="qm-main-page">
        <TabBar
          unselectedTintColor="#949494"
          tintColor="#77CC99"
          barTintColor="white"
          hidden={this.state.hidden}
        >
          <TabBar.Item
            title="主页"
            key="主页"
            icon={<HomeOutlined className="mainIcon"/>}
            selectedIcon={<HomeOutlined className="mainIcon"/>}
            selected={this.state.selectedTab === 'blueTab'}
            onPress={() => {
              this.props.history.push('/main/task')
              this.setState({
                selectedTab: 'blueTab',
              });
            }}
            data-seed="logId"
          >
           <Home/>
          </TabBar.Item>
          <TabBar.Item
            icon={<CopyOutlined className="mainIcon"/>
            }
            selectedIcon={<CopyOutlined className="mainIcon"/>
            }
            title="任务"
            key="任务"

            selected={this.state.selectedTab === 'greenTab'}
            onPress={() => {
              this.props.history.push('/main/task')
              this.setState({
                selectedTab: 'greenTab',
              });
            }}
          >
            <Task/>
          </TabBar.Item>

          <TabBar.Item
            icon={<UsergroupAddOutlined className="mainIcon"/>}
            selectedIcon={<UserOutlined className="mainIcon"/>}
            title="我的"
            key="我的"
            selected={this.state.selectedTab === 'yellowTab'}
            onPress={() => {
              this.props.history.push('/main/my')
              this.setState({
                selectedTab: 'yellowTab',
              });
            }}
          >
            <My/>
          </TabBar.Item>

        </TabBar>
      </div>
    )
  }
};

export default Main;
