import { TabBar } from 'antd-mobile';
import React,{Component}from 'react'
import Home from '@/components/main/Home'
import My from '@/components/main/My'
import Task from '@/components/main/Task'
import {HomeOutlined,UserOutlined,CopyOutlined, UsergroupAddOutlined } from '@ant-design/icons'
import NavHeader from '@/components/common/NavHeader'
import {mainPageMap}from '@/plugins/libs/routerMap'

class Main extends Component{
  constructor(...args) {
    super(...args);
    this.state = {
      selectedTab: 'home',
      hidden: false
    };
  }
  componentDidMount(){
    this.setState({
      selectedTab:this.props.match.params.type
    })
  }
  render(){
    return (
      <div className="qm-main-page">
        <NavHeader path={this.props.match}/>
        <TabBar
          unselectedTintColor="#949494"
          tintColor="#77CC99"
          barTintColor="white"
          hidden={this.state.hidden}
        >
          {mainPageMap.map((item,index)=>{
            return(
              <TabBar.Item
                title={item.title}
                key={index}
                icon={item.icon}
                selectedIcon={item.icon}
                selected={this.state.selectedTab === item.type}
                onPress={() => {
                  this.props.history.push(`/main/${item.type}`)
                  this.setState({
                    selectedTab: item.type,
                  });
                }}
              >
              {}
              </TabBar.Item>
            )
          })}

          <TabBar.Item
            icon={<CopyOutlined className="mainIcon"/>
            }
            selectedIcon={<CopyOutlined className="mainIcon"/>
            }
            title="任务"
            key="任务"

            selected={this.state.selectedTab === 'task'}
            onPress={() => {
              this.props.history.push('/main/task')
              this.setState({
                selectedTab: 'task',
              });
            }}
          >
            <Task/>
          </TabBar.Item>

          <TabBar.Item
            icon={<UsergroupAddOutlined className="mainIcon"/>}
            selectedIcon={<UserOutlined className="mainIcon"/>}
            title="我的"
            key="我的"
            selected={this.state.selectedTab === 'my'}
            onPress={() => {
              this.props.history.push('/main/my')
              this.setState({
                selectedTab: 'my',
              });
            }}
          >
            <My/>
          </TabBar.Item>

        </TabBar>
      </div>
    )
  }
};

export default Main;
